import { Component, Prop, Vue } from 'vue-property-decorator';
import Stopwatch, { StopwatchCallback } from '../services/Stopwatch';

@Component
export default class HelloWorld extends Vue {
  @Prop() private person!: string;
  @Prop() private talkingPerson!: string;
  @Prop() private setTalkingPerson!: Function;
  @Prop() private removePerson!: Function;

  private talking: boolean = false;
  private currentTimeInSeconds: string = "0";
  private stopwatch = new Stopwatch();

  private toggle(): void {
    if (this.talking === false) {
      this.talk();
    } else {
      this.listen();
    }
  }
  private talk(): void {
    this.setTalkingPerson(this.person);
    this.talking = true;
    this.stopwatch.start((time: number) => {
       this.updateCurrentTimeInSeconds(time);
    });
  }
  private listen(): void {
    this.talking = false;
    this.stopwatch.stop();
    this.setTalkingPerson(null);
  }
  private updateCurrentTimeInSeconds(time: number): void {
    if (this.talkingPerson === this.person) {
      this.currentTimeInSeconds = Number(time).toFixed(1);
    } else {
      this.listen();
    }
  }
}
