import { Component, Vue } from 'vue-property-decorator';
import HelloWorld from '@/components/HelloWorld.vue'; // @ is an alias to /src
import Pool from '@/components/Pool.vue';
import * as Collections from 'typescript-collections';
import Stopwatch from '../services/Stopwatch';

@Component({
  components: {
    Pool,
  },
})
export default class StandUp extends Vue {

  private people = new Collections.Set<string>();
  private peopleArray: string[] = [];

  private talkingPerson: string|null = null;

  private newPersonName: string|null = null;

  private mainStopwatch = new Stopwatch();

  private mounted() {
      this.people.add('Jozef');
      this.people.add('Peter');

      this.peopleArray = this.people.toArray().sort();
  }

  private setTalkingPerson(person: string): void {
    this.talkingPerson = person;

    if(this.talkingPerson !== null) {
      this.mainStopwatch.start((time:number)=>{
        console.log('time', time);
      });
    } else {
      this.mainStopwatch.stop();
    }
  }
  private removePerson(person: string): void {
    if (this.people.contains(person)) {
      this.people.remove(person);
      this.setPeopleToPeopleArray();
    }
  }
  private addPerson() {
    if ( this.newPersonName !== null ) {
      if (this.people.contains(this.newPersonName) === false) {
        this.people.add(this.newPersonName);
        this.setPeopleToPeopleArray();
      } else {
        this.$swal(`${this.newPersonName} already exists!`,`Choose different name.`,`OK`);
      }
      this.newPersonName = null;
    }
  }

  private setPeopleToPeopleArray() {
    this.peopleArray = this.people.toArray().sort();
  }
}
